INSERT INTO `categories` (`id`, `slug`, `name`, `description`, `using`) VALUES
(1, 'mega-sumo', 'Mega Sumo', 'Large autonomous robots (up to 3 kg) engage in high-power tactical duels. Strength, control, and balance are key.', 1),
(2, 'mini-sumo', 'Mini Sumo', 'Compact 500g robots compete head-to-head in pushing opponents out of the ring. Fast reactions, clean strategy.', 1),
(3, 'micro-sumo', 'Micro Sumo', 'Smaller and faster — 100g robots battle in tiny arenas. Precision and minimalism at their best.', 1),
(4, 'humanoid-sumo', 'Humanoid Sumo', 'Two-legged robots mimic human-style battles, pushing each other with stability and balance. Pure entertainment meets tech.', 1),
(5, 'humanoid-challenge', 'Humanoid Challenge', 'Humanoid robots run, climb, and push in a dynamic course of 3 physical challenges. A true engineering and software feat.', 1),
(6, 'line-follower-classic', 'Line Follower Classic', 'Robots follow a black line on a white background using sensors and algorithms. Simple track, maximum precision.', 1),
(7, 'line-follower-turbo', 'Line Follower Turbo', 'Same track, more speed. Robots must keep stability while moving at high velocity — the race against physics.', 1),
(8, 'line-follower-enhanced', 'Line Follower Enhanced', 'Multiple forks, dead ends, complex intersections. AI-powered navigation and smart decision-making are essential.', 1),
(9, 'drag-race', 'Drag Race', 'A straight-line acceleration race. Robots built for speed compete to cover a short distance in the shortest time possible.', 1),
(10, 'lego-sumo-3kg', 'Lego Sumo 3kg', 'Powerful LEGO-built robots (up to 3 kg) clash in fully autonomous sumo matches. Tactics, stability, and smart engineering win the ring.', 1),
(11, 'lego-line-follower', 'Lego Line Follower', 'LEGO-built robots follow a marked line autonomously. Precision, speed, and stable control are the keys to success.', 1),
(12, 'mblock-line-follower', 'Mblock Line Follower', 'Robots programmed in mBlock track a line on the arena. Block-based coding meets accuracy and smooth navigation.', 1),
(13, 'lego-sumo-1kg', 'Lego Sumo 1kg', 'LEGO robots up to 1 kg face off in the ring. Push, outsmart, and outlast the opponent with power and balance.', 1),
(14, 'autonomus-car-race', 'Autonomous Car Race', 'Self-driving robots race on a predefined track. Navigation, sensors, and speed decide the winner.', 1),
(15, 'maze-solving', 'Maze Solving', 'Robots must escape a maze in minimum time. Smart algorithms and accurate sensing make the difference.', 1),
(17, 'autonomus-drone-race', 'Autonomous Drone Race', 'Drones fly a marked track fully autonomously. Stability, obstacle navigation, and precise control are essential.', 1),
(18, 'rally-drone-race', 'Rally Drone Race', 'Autonomous drones navigate a netted arena with obstacles and gates. Accuracy and clean flight bring victory.', 1),
(19, 'footbot-lego-league', 'FootBot Lego League', 'Autonomous robots play soccer on the field. Vision, control, and tactics turn the match in their favor.', 1);
INSERT INTO `changelogs` (`id`, `slug`, `title`, `description`, `area`, `version`, `is_breaking`, `visible_to`, `posted_by_admin_id`, `status`, `is_pinned`, `created_at`, `updated_at`, `scheduled_at`, `published_at`) VALUES
(1, 'scoreengine-v2-beta-admin', 'ScoreEngine v2.0 · Beta (Admin)', '<h6 class=\"mb-2\">Overview</h6><p><strong>ScoreEngine v2.0</strong> powers the Relativity Robotics Challenge platform. This Open Beta ships a modernized stack across <strong>Frontend</strong>, <strong>UCP</strong>, <strong>Admin Area</strong>, and the <strong>Scoring System</strong>, with a focus on stability, transparency and scale.</p><h6 class=\"mb-2\">Frontend</h6><ul class=\"bullet-inside ps-0\"><li>Faster navigation with component lazy-loading and route-level code splitting.</li><li>Improved accessibility and keyboard support on key flows.</li><li>Optimized assets for lower time-to-interactive during peak traffic.</li></ul><h6 class=\"mb-2\">UCP (User Control Panel)</h6><ul class=\"bullet-inside ps-0\"><li>Clearer flows for teams, members and robots with inline validation.</li><li>Stricter category rules and age checks with instant feedback.</li><li>CSV import helper with template hints and row-by-row error preview.</li></ul><h6 class=\"mb-2\">Admin Area</h6><ul class=\"bullet-inside ps-0\"><li>Fast lists with smart pagination and advanced filters.</li><li>Bulk actions for common operations and activity log for traceability.</li><li>Audit-friendly views for sensitive updates.</li></ul><h6 class=\"mb-2\">Scoring System</h6><ul class=\"bullet-inside ps-0\"><li>Multi-step scoring pipeline with duplicate prevention and conflict checks.</li><li>Per-action history with operator, reason and timestamp.</li><li>Quick export for jury review and post-event reports.</li></ul><h6 class=\"mb-2\">Why it matters</h6><ul class=\"bullet-inside ps-0\"><li>Resilience under load: designed for traffic spikes during competition days.</li><li>Modular updates: ship parts independently without blocking the platform.</li><li>Transparency by default: scoring changes are explainable and auditable.</li></ul><h6 class=\"mb-2\">Status</h6><p><em>Beta</em>: all core features are available. Minor UI/UX polish and performance tweaks will continue as we gather feedback.</p>', 'ucp', '2.0.0-beta', 0, 'admin', 1, 'published', 0, '2025-08-30 22:04:48', '2025-09-05 23:57:21', NULL, '2025-08-30 22:04:48'),
(2, 'scoreengine-v2-beta', 'ScoreEngine v2.0 · Beta is now live', '<h6 class=\"mb-2\">Overview</h6><p>We have released <strong>ScoreEngine v2.0</strong>, the new engine powering Relativity Robotics Challenge. It brings improvements to the website, User Control Panel, Admin Area, and the scoring system.</p><h6 class=\"mb-2\">What’s new for you</h6><ul class=\"bullet-inside ps-0\"><li>Faster and smoother website experience.</li><li>Clearer team and robot management in UCP.</li><li>More reliable scoring system during matches.</li></ul><h6 class=\"mb-2\">Status</h6><p><em>Beta</em>: All main features are available. We will continue improving the experience with small updates.</p>', 'ucp', '2.0.0-beta', 0, 'all', 1, 'published', 0, '2025-08-30 22:04:53', '2025-09-05 23:55:41', NULL, '2025-08-30 22:04:53'),
(3, 'robot-form-category-validation-admin', 'Robot add form · single-member auto-assign & multi-category support (Admin)', '<h6 class=mb-2>Changes & fixes</h6>\n     <h6 class=mb-2>Robot add form</h6>\n     <ul class=bullet-inside ps-0>\n       <li>If a team has <strong>only one member</strong>, that member is automatically assigned as both <strong>operator</strong> and <strong>robot member</strong>.</li>\n       <li>Operator and members selection fields are hidden in this case.</li>\n       <li>An informational message explains that no manual selection is needed.</li>\n     </ul>\n     <h6 class=mb-2>Form submission</h6>\n     <ul class=bullet-inside ps-0>\n       <li>In JavaScript, when submitting the form, the single member is automatically included in the payload sent to the backend.</li>\n       <li>This prevents the error: <em>Please select at least one robot member.</em></li>\n     </ul>\n     <h6 class=mb-2>Category validation</h6>\n     <ul class=bullet-inside ps-0>\n       <li>Backend validation updated: now allows selecting <strong>one or multiple categories</strong> instead of only one.</li>\n       <li>Multi-category selection is correctly accepted and saved.</li>\n     </ul>\n     <h6 class=mb-2>Result</h6>\n     <p>The form now works correctly for both single-member and multi-member teams, and multi-category selection is supported without errors.</p>', 'registration', '2.0.2', 0, 'admin', 1, 'published', 0, '2025-09-01 21:23:16', '2025-09-01 21:24:19', NULL, '2025-09-01 21:23:16'),
(4, 'robot-form-category-validation', 'Robot add form improvements: single-member auto-assign & multi-category', '<h6 class=mb-2>What’s new</h6>\n     <ul class=bullet-inside ps-0>\n       <li>If your team has <strong>only one member</strong>, that member is now automatically set as <strong>operator</strong> and <strong>robot member</strong>. You no longer need to select them manually.</li>\n       <li>The error about missing robot members will no longer appear.</li>\n       <li>You can now select <strong>multiple categories</strong> for your robot, and they will be saved correctly.</li>\n     </ul>\n     <h6 class=mb-2>Result</h6>\n     <p>The form works correctly for both small and large teams, and multi-category selection is fully supported.</p>', 'registration', '2.0.2', 0, 'all', 1, 'published', 0, '2025-09-01 21:23:24', '2025-09-01 21:24:29', NULL, '2025-09-01 21:23:24'),
(5, 'v2-0-2-admin', 'v2.0.2 — Admin Area Update', '<h6 class=\"mb-2\">Overview</h6><p>We shipped <strong>v2.0.2</strong> for the Admin Area of the Relativity Robotics Challenge platform. This release adds new management modules, export tools, homepage statistics, and a hardened licensing layer managed by EssenByte Solutions.</p><h6 class=\"mb-2\">What’s new for you</h6><ul class=\"bullet-inside ps-0\"><li>New management modules: <strong>Coaches</strong>, <strong>Teams</strong>, <strong>Members</strong>, <strong>Robots</strong>.</li><li>Developer area: <strong>Dev Changelogs</strong> available in admin.</li><li>Homepage: <strong>competition statistics</strong> surfaced for quick insights.</li><li>Security: site is now <strong>licensed by EssenByte</strong>; licensing system lives in <code>/lib/License</code>.</li><li>Licensed &amp; encrypted files:<ul class=\"bullet-inside ps-0\"><li><code>/index.php</code></li><li><code>/lib/License/LicenseClient.php</code></li><li><code>/lib/License/LicenseServer.php</code></li></ul></li><li>Data export (list, PDF, CSV) for:<ul class=\"bullet-inside ps-0\"><li>Coaches / Managers</li><li>Members</li><li>Teams</li><li>Robots</li><li>Coaches &amp; Members (combined)</li><li>Complete dataset</li></ul></li></ul><h6 class=\"mb-2\">Status</h6><p><em>Stable</em>: All features are live. We’ll continue incremental polish and performance tweaks.</p>', 'ucp', '2.0.2', 0, 'admin', 1, 'published', 0, '2025-09-05 23:58:10', '2025-09-05 23:58:22', NULL, '2025-09-05 23:58:10'),
(6, 'v2-0-2-users', 'v2.0.2 — User Improvements', '<h6 class=\"mb-2\">Overview</h6><p><strong>v2.0.2</strong> includes quality-of-life improvements for participants and team managers, focusing on profile controls and reliability for single-member teams.</p><h6 class=\"mb-2\">What’s new for you</h6><ul class=\"bullet-inside ps-0\"><li>New <strong>Settings</strong> page to update your profile information.</li><li>Fixes for <strong>adding and editing members</strong> in single-member teams.</li></ul><h6 class=\"mb-2\">Status</h6><p><em>Stable</em>: Available to all users. Further minor refinements will roll out continuously.</p>', 'ucp', '2.0.2', 0, 'all', 1, 'published', 0, '2025-09-05 23:58:15', '2025-09-05 23:58:15', NULL, '2025-09-05 23:58:15'),
(10, 'v2-1-0-admin', 'v2.1.0 — Admin Platform Update', '<h6 class=\"mb-2\">Overview</h6>\r\n<p>We shipped <strong>v2.1.0</strong> for the Admin Area of the Relativity Robotics Challenge platform. This release strengthens control over regulations and introduces developer tools for changelog management.</p>\r\n\r\n<h6 class=\"mb-2\">What’s new for you</h6>\r\n<ul class=\"bullet-inside ps-0\">\r\n<li><strong>Developer area</strong>: upload and manage changelogs directly inside admin.</li>\r\n<li><strong>Manage Regulations</strong>: simplified editor to update and maintain competition regulations.</li>\r\n<li><strong>Backend optimizations</strong>: reduced error risk and stabilized regulation publishing.</li>\r\n<li><strong>Migration prep</strong>: backend adjusted to support server transfer window.</li>\r\n</ul>\r\n\r\n<h6 class=\"mb-2\">Important Notice — EssenByte Solutions</h6>\r\n<p>During server migration (2025-09-15 → 2025-09-25), all outgoing emails will temporarily be sent from:</p>\r\n<ul class=\"bullet-inside ps-0\">\r\n<li>info@essenbyte.com</li>\r\n<li>office@essenbyte.com</li>\r\n<li>noreply@essenbyte.com</li>\r\n<li>relativity@essenbyte.com</li>\r\n<li>office@relativity.ro</li>\r\n</ul>\r\n<p>Admins should verify deliverability and whitelist these addresses.</p>\r\n\r\n<h6 class=\"mb-2\">Status</h6>\r\n<p><em>Stable</em>: All features are live and operational.</p>', '', '2.1.0', 0, 'admin', 1, 'published', 0, '2025-09-20 01:45:38', '2025-09-20 01:45:38', NULL, '2025-09-20 01:45:38'),
(11, 'v2-1-0-users', 'v2.1.0 — User Platform Update', '<h6 class=\"mb-2\">Overview</h6>\r\n<p><strong>v2.1.0</strong> includes major improvements for all participants, introducing a new information hub, fixing logo issues, and fully revising competition regulations.</p>\r\n\r\n<h6 class=\"mb-2\">What’s new for you</h6>\r\n<ul class=\"bullet-inside ps-0\">\r\n<li>New <strong>Informations section</strong> with Event Details, Timeline, Deadline, Eligibility, Fees, Travel, and Accommodation.</li>\r\n<li><strong>Team logo fix</strong>: resolved issue where uploaded logos were being lost.</li>\r\n<li><strong>Regulations update</strong>: all competition regulations have been revised. Participants are required to re-read them carefully.</li>\r\n</ul>\r\n\r\n<h6 class=\"mb-2\">Important Notice — EssenByte Solutions</h6>\r\n<p>During server migration (2025-09-15 → 2025-09-25), all outgoing emails will temporarily be sent from:</p>\r\n<ul class=\"bullet-inside ps-0\">\r\n<li>info@essenbyte.com</li>\r\n<li>office@essenbyte.com</li>\r\n<li>noreply@essenbyte.com</li>\r\n<li>relativity@essenbyte.com</li>\r\n<li>office@relativity.ro</li>\r\n</ul>\r\n<p>Please whitelist these addresses to avoid communication issues.</p>\r\n\r\n<h6 class=\"mb-2\">Status</h6>\r\n<p><em>Stable</em>: Available to all users. Continuous refinements will follow.</p>', 'ucp', '2.1.0', 0, 'all', 1, 'published', 0, '2025-09-20 01:45:42', '2025-09-20 01:45:42', NULL, '2025-09-20 01:45:42');
